import React from 'react'
class Play extends React.Component {
    render() {
        const on = this.props.on ? "start" : "pause";
        return (
            <div className="play">
                <button className="btn playBtn" onClick={this.props.triggerTimer}>
                    {on}
                    <i className="fa fa-play" />
                </button>
            </div>
        )
    }
}
export default Play;
