import React, { Component } from "react";
import Button from "./Button";
import "./timer.css";
import Display from './Display'

// https://codepen.io/peterbe/pen/LNxmRp?editors=1010

class Timer extends Component {
  constructor(props) {
    super(props);

    this.state = { on: false, timeMin: 0, timeSec: 0, timeMil: 0 };
    this.timerId = 0;
  }
  render() {
    const title = this.state.on ? "Pause" : "Start";
    const seconds = this.state.timeSec;
    const minutes = this.state.timeMin;
    return (
      <div>
        <Display seconds={seconds} minutes={minutes} />

        <button
          className="btn playBtn"
          title={title}
          onClick={this.triggerTimer.bind(this)}
        >
          <i className="fa fa-play" />
        </button>
        <Button title="Reset" onClick={this.resetTimer.bind(this)} />
      </div>
    );
  }

  triggerTimer() {
    console.log("start");
    this.state.on ? this.pauseTimer() : this.startTimer();
  }

  startTimer() {
    let { timeSec, timeMil } = this.state;
    this.setState({ on: true });
    this.timerId = setInterval(() => {
      if (this.state.timeMil === 10) {
        this.setState({
          timeSec: (timeSec += 1),
          timeMil: (timeMil = 0)
        });
      } else {
        this.setState({ timeMil: (timeMil += 1) });
      }
    }, 100);
  }

  pauseTimer() {
    this.setState({ on: false });
    this.clearTimer();
  }

  resetTimer() {
    this.clearTimer();
    this.setState({ on: false, timeSec: 0, timeMil: 0 });
  }

  componentWillUnmount() {
    this.clearTimer();
  }

  clearTimer() {
    clearInterval(this.timerId);
  }
}

export default Timer;
